def add(a, b):  ## these parameters are formal parameters
    """This is for summation of a and b
    a: give value of a
    b: give value of b
    """
    
    return f"The addition of {a} and {b} is {a+b}"
    
def mul(a, b):  ## these parameters are formal parameters
    """This is for multiplication of a and b
    a: give value of a
    b: give value of b
    """
    
    return f"The addition of {a} and {b} is {a*b}"
    